import { TevmClient } from '@tevm/client-types';

/**
 * @deprecated a new http client will be created in a future version. For now it's recomended to use viem
 */
type HttpClient = TevmClient & {
    /**
     * The url being used to connect to the remote Tevm backend
     */
    url: string;
    /**
     * Name of the client
     */
    name: string;
};

/**
 * @deprecated
 * Options for a HttpClient
 */
type HttpClientOptions = {
    /**
     * Remote URL to connect to
     */
    readonly url: string;
    /**
     * Optional name for the client
     */
    readonly name?: string;
};

declare function createHttpClient({ url, name }: HttpClientOptions): HttpClient;

export { type HttpClient, type HttpClientOptions, createHttpClient };
